import React from 'react';

export default class Tabela extends React.Component {
    render() {
        const nomes = this.props.lista.map((pessoas, index) => 
            <tr key = {index} onContextMenu = {this.props.removerPessoa.bind(this, index)}>
                <td> {pessoas.nome} </td>
            </tr>
        );

        const listPessoas = nomes;

        return (
            <ol>
                { listPessoas.map(pessoa => <li>{ pessoa }</li>).sort()}
            </ol>
        )
    }
}











