import React from 'react';

export default class Tabela extends React.Component {
    render() {
        
        const nomes = this.props.lista.map((pessoas, index) => 
            <tr key = {index} onContextMenu = {this.props.removerPessoa.bind(this, index)}>
                <td> {index} </td>
                <td> {pessoas.nome} </td>
            </tr>
        );
        
        const listPessoas = nomes;
        const listaPessoas = listPessoas.sort((a, b) => a.nomes > b.nomes).map(
            (nomes, index) => <div key={index}> {nomes} </div>
        )
        
        

        return (
            <ol>
                { listaPessoas.map(pessoa => <li>{ pessoa }</li>)}
            </ol>
        )
    }
}











