import React from 'react';
import Tabela from './Tabela';

export default class Form extends React.Component {

    limpar = () => {
        this.setState({
            nome: ''
        });
    }

    submiter = (evento) => {
        evento.preventDefault();
        console.log('submeter');
        
    }

    render() {
        return ( 
            <form onSubmit = {this.submiter} onReset = {this.limpar}>
                <div>
                    <label className = 'nome'> Nome </label>
                    <input type = 'text' name = 'nome' value = {this.state.nome}
                        id = 'input' onChange = {this.alterar} />
                </div>

                <div className = 'botoes'>
                    <button type = 'submit' id = 'botao' disabled={!this.state.nome} > Salvar </button>
                    <button id='limpar' type = 'reset'> Limpar </button>
                </div>

            </form>
        
        );
    }

