import React from 'react';

export default class Tabela extends React.Component {
    render() {
        /*
        const sorted = this.props.lista.sort();
        const nomes = sorted.map((pessoas, index) => 
                <div> {pessoas.nome} </div>
        );
        
        const listPessoas = nomes;
*/
        // array temporário que armazena os objetos com o índice e o valor para ordenação
var mapped = this.props.lista.map(function(el, i) {
    return { index: i, value: el.toLowerCase() };
  })
  
  // ordenando o array mapeado contendo os dados resumidos
  mapped.sort(function(a, b) {
    return +(a.value > b.value) || +(a.value === b.value) - 1;
  });
  
  // containerpara o resultado ordenado
  var result = mapped.map(function(el){
    return this.props.lista[el.index];
  });

        return (
            <ol>
                { result.map(pessoa => <li>{ pessoa }</li>)}
            </ol>
        )
    }
}
