import React from 'react';
import Tabela from './Tabela';

export default class Form extends React.Component {
    constructor() {
        super();
//        this.state = { value: '' };
        this.changeNome = this.changeNome.bind(this);
        this.add = this.add.bind(this);
        this.state = {
            nome: '',
            pessoas: []
        };
    }

    add() {
        this.props.onButtonClick(this.state.nome);
        this.setState({ nome: '' });
      }

    changeNome = event => {
        this.setState({ nome: event.target.nome });
       this.setState ({
           nome: event.target.value.toLowerCase().replace(/(?:^|\s)\S/g, 
           function(a) { return a.toUpperCase(); })
        });
    };

    remover = (indice, event) => { // Deleta o elemento por seu id
        event.preventDefault();
        let temp = [...this.state.pessoas]; // cria uma cópia do array
        temp.splice(indice, 1); // remove 1 elemento a partir da posição indice
        this.setState({
            pessoas: temp
        });
    }
    
    reset = event => {
        event.preventDefault();
        this.setState({
            nome: ''
        });
    }

    submit = event => {
        event.preventDefault();
        if (this.state.nome.trim() !== '') {
            this.state.pessoas.push({
                nome: this.state.nome
            });
            this.setState({
                errorMsg: ""
            });
            event.target.reset();
            this.state.pessoas.sort((a, b) => { return a.nome > b.nome});
        } else{
            this.setState({ errorMsg: "O campo nome é obrigatório!"});
        }
        
    }
    

    render() {
        return (
            <div>
                <form onSubmit = {this.submit} onReset = {this.reset}>
                    <div>
                        <label> Nome </label>
                        <input type = 'text' name = 'nome' value = {this.state.nome}
                            onChange = {this.changeNome} id = 'input' placeholder={this.props.placeholder} />
                    </div>

                    <div>
                        <button type = 'submit' id = 'botao' disabled={!this.state.value} onClick={this.add} > Add </button>
                        <button type = 'reset'> Limpar </button>
                    </div>

                    <div>
                        {this.state.errorMsg}
                    </div>
                </form>
                <Tabela lista = {this.state.pessoas} removerPessoa = {this.remover} />
            </div>
        );
    }
}
